current.custom_css:
.btn, .sf__btn {
font-family: var(--font-stack-header);
}
.rounded,
.form-control {
  border-radius: 0;
}

.f-price {font-weight: 400;}

.content__subheading,
.section__subheading {
color: var(--color-primary);
letter-spacing: 0.5px;
    font-size: 15px;
}

.sf-footer form.sf__footer-form-newsletter .sf__newletter__form-input {
background-color: #504D48;
    color: #fff;
}
.sf-footer .social-media-links a {
  color: #999;
  padding: 0 20px;
}

.sf-footer .social-media-links a:hover {
  color: #fff;
}
.sf__accordion-item .sf__accordion-button:after {
  background-image: var(--arrow-down-white-url);
}
@media ( min-width: 768px ) {
.sf__footer-block-newsletter .sf__footer-block-title {
font-size: 36px;
}
.sf__footer-block-newsletter .sf__footer-block-title {
text-transform: uppercase;
}
}
